export default class{constructor(t){this.materials={},this.stage=t}getMaterial(t){return this.materials[t]}addMaterial(t,s){this.materials[t]=s,this.stage.add(3,s)}dispose(){for(const t in this.materials)this.stage.remove(3,this.materials[t].id);this.materials={}}}
